package hackntu2015.edu.yzu.drivertaipei;import android.app.Activity;import android.content.Intent;import android.net.Uri;import android.os.Bundle;import android.view.View;import android.widget.AdapterView;import android.widget.ImageButton;import android.widget.ImageView;import android.widget.ListView;import com.navdrawer.SimpleSideDrawer;import hackntu2015.edu.yzu.drivertaipei.utils.Utils;public class AboutActivity extends Activity {    ImageButton bugReport;    ImageButton back;    ImageButton officialLink;    ImageButton menu;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_about);        addBugReport();        addBack();        addOfficialLink();        menu = (ImageButton)findViewById(R.id.btn_menu);        final SimpleSideDrawer mMenu = new SimpleSideDrawer(this);        mMenu.setLeftBehindContentView(R.layout.menu);        menu.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View v) {                mMenu.toggleLeftDrawer();            }        });        ImageView menu_top = (ImageView)mMenu.findViewById(R.id.menu_top);        if(Utils.isNight()) {            menu_top.setBackgroundResource(R.mipmap.menu_topimage_night);        } else{            menu_top.setBackgroundResource(R.mipmap.menu_topimage_day);        }        ListView listView = (ListView) mMenu.findViewById(R.id.listView);        listView.setAdapter(new MenuAdapter(this));        listView.setOnItemClickListener(new AdapterView.OnItemClickListener() {            @Override            public void onItemClick(AdapterView<?> parent, View view, int position, long id) {                switch (position) {                    case 0:                        finish();                        overridePendingTransition(android.R.anim.fade_in, android.R.anim.fade_out);                        break;                    case 1:                        startActivity(new Intent(AboutActivity.this, SettingActivity.class));                        overridePendingTransition(android.R.anim.fade_in, android.R.anim.fade_out);                        finish();                        break;                    case 2:                        mMenu.toggleLeftDrawer();                        break;                    default:                        break;                }            }        });    }    private void addBugReport() {        bugReport = (ImageButton) findViewById(R.id.about_button_bugreport);        bugReport.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View v) {            }        });    }    private void addBack(){        back = (ImageButton) findViewById(R.id.about_button_back);        back.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View v) {                finish();            }        });    }    private void addOfficialLink(){        officialLink = (ImageButton) findViewById(R.id.about_button_officiallink);        officialLink.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View v) {                Uri uri = Uri.parse("https://www.facebook.com/yzuitac");                Intent intent = new Intent(Intent.ACTION_VIEW,uri);                startActivity(intent);            }        });    }}