package hackntu2015.edu.yzu.drivertaipei;import android.app.Activity;import android.content.Intent;import android.net.Uri;import android.os.Bundle;import android.view.View;import android.widget.ImageButton;public class about extends Activity {    ImageButton bugReport;    ImageButton back;    ImageButton officialLink;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_about);        addBugReport();        addBack();        addOfficialLink();    }    private void addBugReport() {        bugReport = (ImageButton) findViewById(R.id.about_button_bugreport);        bugReport.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View v) {            }        });    }    private void addBack(){        back = (ImageButton) findViewById(R.id.about_button_back);        back.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View v) {                finish();            }        });    }    private void addOfficialLink(){        officialLink = (ImageButton) findViewById(R.id.about_button_officiallink);        officialLink.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View v) {                Uri uri = Uri.parse("https://www.facebook.com/yzuitac");                Intent intent = new Intent(Intent.ACTION_VIEW,uri);                startActivity(intent);            }        });    }}